import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { startTimer } from "../redux/actions";

let timerStarted = false;

const Timer = () => {
  const time = useSelector((state) => state.time);
  const current_player = useSelector((state) => state.current_player);
  const start = useSelector((state) => state.start);
  console.log("start in timer", start);

  //   useEffect(() => {
  //     console.log("current_player in timer", current_player);
  //     if (current_player !== null) dispatch(startTimer(5));
  //   }, [dispatch, current_player]);

  return (
    <>
      <h1>Timer</h1>
      {start !== "end" && <p>{time}</p>}
    </>
  );
};

export default Timer;
