import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";

const Sketch = (p5) => {
  const data = useSelector((state) => state.sketch);
  const dispatch = useDispatch();
  let socket;
  p5.setup = () => {
    p5.createCanvas(670, 600);
    p5.background(250);
    p5.line(data.mouseX, data.mouseY, data.pmouseX, data.pmouseY);
  };

  p5.myCustomRedrawAccordingToNewPropsHandler = (props) => {
    if (props.socket) {
      socket = props.socket;
    }
  };

  p5.clear = (newProp) => {
    if (newProp.clear) {
      p5.clear();
      p5.background(250);
    }
  };

  p5.draw = () => {};

  p5.mouseDragged = function () {
    p5.strokeWeight(10);
    p5.line(p5.mouseX, p5.mouseY, p5.pmouseX, p5.pmouseY);
    dispatch(
      startToDraw({
        mouseX: p5.mouseX,
        mouseY: p5.mouseY,
        pmouseX: p5.pmouseX,
        pmouseY: p5.pmouseY,
      })
    );
  };
};

export default Sketch;
