import React, { useState } from "react";
import { startToDraw } from "../../redux/actions";
import { useDispatch, useSelector } from "react-redux";

const Sketch = (p5) => {
  let data;
  let dispatch;
  p5.setup = () => {
    p5.createCanvas(670, 600);
    p5.background(250);
    console.log("data in setup", data);
    if (props.data) {
      console.log("data in not null");
      p5.line(data.mouseX, data.mouseY, data.pmouseX, data.pmouseY);
    }
  };

  p5.myCustomRedrawAccordingToNewPropsHandler = (props) => {
    console.log("prop", props);
    if (props.clear) {
      p5.clear();
      p5.background(250);
    }
    if (props.data) {
      data = props.data;
      console.log("data", data);
    }
    dispatch = props.dispatch;
  };

  p5.draw = () => {};

  p5.mouseDragged = function () {
    p5.strokeWeight(10);
    p5.line(p5.mouseX, p5.mouseY, p5.pmouseX, p5.pmouseY);
    dispatch(
      startToDraw({
        mouseX: p5.mouseX,
        mouseY: p5.mouseY,
        pmouseX: p5.pmouseX,
        pmouseY: p5.pmouseY,
      })
    );
  };
};

export default Sketch;
