import { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import { connectToChat, storeUser } from "../redux/actions";
import { io } from "socket.io-client";

const JoinChat = () => {
  const [username, setUsername] = useState("");
  const [isValid, setIsValid] = useState(null);
  const [game, setGame] = useState("");

  const dispatch = useDispatch();

  useEffect(() => {
    let socket = io.connect("localhost:4002");
    socket.on("game", (data) => {
      setGame(data);
    });
    console.log("game", game);
  }, []);

  const processInput = (event) => {
    if (event.keyCode !== 13) {
      if (event.target.value.length > 0) {
        setUsername(event.target.value);
        setIsValid(true);
      } else {
        setIsValid(false);
        setUsername(event.target.value);
      }
    }
  };

  const verifyUsername = () => {
    if (username.length > 0) {
      setIsValid(true);
      dispatch(connectToChat(username));
      dispatch(storeUser(username));
    } else {
      setIsValid(false);
    }
  };

  const onKeyUp = (event) => {
    if (event.keyCode === 13) verifyUsername();
  };

  return (
    <form className="my-4" onSubmit={(e) => e.preventDefault()}>
      <div className="row">
        <div className="col">
          <input
            aria-label="Your username"
            type="text"
            className="form-control has-validation"
            id="username"
            invalid={`${isValid === false}`}
            valid={`${isValid === true}`}
            placeholder="Username"
            value={username}
            onChange={processInput}
            onKeyUp={onKeyUp}
          />
          <div
            className="invalid-feedback"
            style={
              isValid === false ? { display: "block" } : { display: "none" }
            }
          >
            Username cannot be empty!
          </div>
        </div>
        <div className="col-auto">
          <button
            type="button"
            className="btn btn-primary float-right"
            onClick={verifyUsername}
            disabled={game === "start"}
          >
            Join Chat
          </button>
        </div>
      </div>
    </form>
  );
};

export default JoinChat;
