import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { startTimer } from "../redux/actions";

let timerStarted = false;

const Timer = () => {
  const time = useSelector((state) => state.time);
  const current_player = useSelector((state) => state.current_player);

  const dispatch = useDispatch();

  useEffect(() => {
    if (current_player !== null) dispatch(startTimer(5));
  }, [dispatch, current_player]);

  return (
    <>
      <h1>Timer</h1>
      <p>{time}</p>
      <p>
        <button onClick={() => dispatch(startTimer(5))}>Start Over</button>
      </p>
    </>
  );
};

export default Timer;
