import { useState } from "react";
import sketch from "../canvas/sketch";

const Draw = () => {
  const [clear, setClear] = useState(false);
  return (
    <>
      <P5Wrapper sketch={sketch} color={this.state.color}></P5Wrapper>
      <button onClick={() => setClear(!clear)}>clear</button>
    </>
  );
};

export default Draw;
