import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";

import JoinChat from "../components/join-chat";
import Canvas from "../components/canvas/canvas";
import Messages from "../components/messages";
import Form from "../components/form";
import { startGame, setStart, updateStart } from "../redux/actions";
import PlayerList from "../components/player-list";
import Timer from "../components/timer";
import Spinner from "../components/spinner";

const App = () => {
  const isConnected = useSelector((state) => state.isConnected);
  const user = useSelector((state) => state.user);
  const start = useSelector((state) => state.start);
  const [clickStart, setClick] = useState(false);
  const current_player = useSelector((state) => state.current_player);
  const clients = useSelector((state) => state.clients);
  const dispatch = useDispatch();

  /* The following is not needed after the extension activities. JoinChat now
   *  handles connecting to the chat.
   *  const dispatch = useDispatch();
   *  useEffect(() => dispatch(connectToChat()), [dispatch]);
   *
   */

  useEffect(() => {
    if (start === "end") {
      setClick(false);
    }
  }, [start]);
  console.log("connected", isConnected);
  console.log("start", start);
  return (
    <div className="container">
      {isConnected ? (
        <>
          <h1>{user}</h1>
          //Current who is drawing, TODO: styling
          {start === "wait" && clickStart && <Spinner />}
          <h1>
            Current: {current_player !== null ? current_player : "game over"}
          </h1>
          <Timer />
          {start !== "start" && (
            <button
              onClick={() => {
                setClick(true);
                dispatch(startGame(0));
                dispatch(updateStart("wait"));
                // dispatch(startTheTimer(5));
                //dispatch(setStart(true));
              }}
            >
              start
            </button>
          )}
          <button
            onClick={() => {
              let index = clients.indexOf(current_player) + 1;
              dispatch(startGame(index));
              // dispatch(startTheTimer(5));
            }}
          >
            Next Player
          </button>
          <div className="row">
            <div className="col-sm-7">
              <Canvas />
            </div>
            {/*Chat window design*/}
            <div className="col-sm-5">
              <Messages />
              <Form />
            </div>
          </div>
          <PlayerList />
        </>
      ) : (
        <JoinChat />
      )}
    </div>
  );
};

export default App;
