import { useDispatch, useSelector } from "react-redux";

import JoinChat from "../components/join-chat";
import Canvas from "../components/canvas/canvas";
import Messages from "../components/messages";
import Form from "../components/form";
import { startGame } from "../redux/actions";

const App = () => {
  const isConnected = useSelector((state) => state.isConnected);
  const user = useSelector((state) => state.user);
  const clients = useSelector((state) => state.clients);
  const current_player = useSelector((state) => state.current_player);
  const dispatch = useDispatch();

  /* The following is not needed after the extension activities. JoinChat now
   *  handles connecting to the chat.
   *  const dispatch = useDispatch();
   *  useEffect(() => dispatch(connectToChat()), [dispatch]);
   *
   */
  console.log("connected", isConnected);
  return (
    <div className="container">
      {isConnected ? (
        <>
          <h1>{user}</h1>
          <h1>{JSON.stringify(clients)}</h1>
          <h1>
            Current: {current_player !== null ? current_player : "game over"}
          </h1>
          <button onClick={() => dispatch(startGame())}>start</button>
          <div className="row">
            <div className="col-sm-7">
              <Canvas />
            </div>
            {/*Chat window design*/}
            <div className="col-sm-5">
              <Messages />
              <Form />
            </div>
          </div>
        </>
      ) : (
        <JoinChat />
      )}
    </div>
  );
};

export default App;
