import React, { useState } from "react";
import P5Wrapper from "react-p5-wrapper";

const Sketch = (p5) => {
  p5.setup = () => {
    p5.createCanvas(670, 600);
    p5.background(250);
  };

  p5.myCustomRedrawAccordingToNewPropsHandler = (newProp) => {
    if (newProp.clear) {
      p5.clear();
    }
  };

  p5.draw = () => {};

  p5.mouseDragged = function () {
    p5.strokeWeight(10);
    p5.line(p5.mouseX, p5.mouseY, p5.pmouseX, p5.pmouseY);
  };
};
